		}else if($process=="doubtnut_p2p"){
			$title="Doubtnut P2P Scrubbed";
		}
		$newfile="QA ".$title." Audit List-'".$currDate."'.csv";

		header('Content-Disposition: attachment;  filename="'.$newfile.'"');
		readfile($filename);
	}

