			$data["qa_craftjack_list"] = $fullAray;
			$this->create_qa_craftjack_CSV($fullAray,$campaign);
			$dn_link = base_url()."reports_qa/download_qa_craftjack_CSV/";
		}

		$data['download_link']=$dn_link;
		$data["action"] = $action;
		$data['date_from'] = $date_from;
