	$filename = "./qa_files/qa_reports_data/Report".get_user_id().".csv";
	$newfile="QA Maalomatia Audit List-'".$currDate."'.csv";
	header('Content-Disposition: attachment;  filename="'.$newfile.'"');
	readfile($filename);
}

