    public function manualColumnProcess($column_val,$comment_val){

			 	$cnt=count($column_val);
				 	$manualColumn="";
			 	if($cnt!=0){
				 	for($i=0;$i<$cnt;$i++){

				 			$manualColumn.='`'.$column_val[$i].'`  varchar(20) NOT NULL COMMENT "'.$comment_val[$i].'",';

				 	}

				 	for($j=1;$j<=$cnt;$j++){

				 			$manualColumn.='`cmt'.$j.'`  TEXT NULL COMMENT "Comments '.$j.'",';

				 	}
			 }

			 return $manualColumn;
    }

