			$row .= '"'.$user['site_name'].'",';
			$row .= '"'.$user['call_id'].'",'; 
			$row .= '"'.$user['agent_feedback'].'",';
			$row .= '"'. str_replace('"',"'",str_replace($searches, "", strip_tags($user['agent_note']))).'",';
			$row .= '"'.$user['mgnt_feedback'].'",';
			$row .= '"'.$user['mgnt_name'].'",';
			$row .= '"'. str_replace('"',"'",str_replace($searches, "", strip_tags($user['mgnt_note']))).'"';				
			
			fwrite($fopen,$row."\r\n");
		}
		
		fclose($fopen);
	}


